```c
typedef struct entry {
    int value;
    struct entry *next;
} Entry;

Entry *insertValue(Entry *entry, int value) {
    /**
        * 在链表中插入一个新项
        * @param entry: 要将新项插入到其后的项的指针
        * @param value: 新项的值
        * @return 新插入项的指针
    */

    Entry *newEntry = (Entry *) malloc(sizeof(Entry));
    newEntry->value = value;
    if (!entry) {
        newEntry->next = NULL;
    } else {
        newEntry->next = entry->next;
        entry->next = newEntry;
    }
    return newEntry;
}
