typedef struct entry {
    int value;
    struct entry *next;
} Entry;

Entry *insertValue(Entry *entry, int value) {
    /**
        * Вставляет новый элемент в список
        * @param entry: указатель на элемент, после которого будет вставлен новый элемент
        * @param value: значение нового элемента
        * @return указатель на новый элемент
    */

    Entry *newEntry = (Entry *) malloc(sizeof(Entry));
    newEntry->value = value;
    if (!entry) {
        newEntry->next = NULL;
    } else {
        newEntry->next = entry->next;
        entry->next = newEntry;
    }
    return newEntry;
}
