typedef struct entry {
    int value;
    struct entry *next;
} Entry;

Entry *insertValue(Entry *entry, int value) {
    /**
        * 在链表中插入一个新项
        * @param entry: 要将新项插入到其后的项的指针
        * @param value: 新项的值
